import React from 'react';
import Table from "./Table";

class Messages extends React.Component {
    constructor(props) {
        super(props);
        this.el = props.ref;
        };
    componentDidMount() {
        this.scrollToBottom();
    }

    componentDidUpdate() {
        this.scrollToBottom();
    }

    scrollToBottom() {
        this.el.scrollIntoView({ behavior: 'smooth' });
    }

    render() {
        return <div ref={el => { this.el = el; }} />
    }
}
export default Messages;
