import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { Transition } from 'react-transition-group';
import './widget.scss';
import ReactDOM from "react-dom";
import Form from "./Form";

class Widget extends Component {
    state = {
        opened: false,
        showDock: true,
        msg: "",
        answ: "",
        text: [],
    }

    handleToggleOpen = () => {
        this.setState((prev) => {
            let { showDock } = prev;
            if (!prev.opened) {
                showDock = false;
            }
            return {
                showDock,
                opened: !prev.opened,
            };
        });
    }

    handleWidgetExit = () => {
        this.setState({
            showDock: true,
        });
    }

    renderBody = () => {
        const { showDock } = this.state;

        if (!showDock) return '';

        return (
            <button
                type="button"
                className="dock"
                onClick={this.handleToggleOpen}
                onKeyPress={this.handleToggleOpen}
            >
                ^ OPEN ^
            </button>
        );
    }

    message = (msg) => {
        this.setState({msg: msg,});
    }

    answer = (answ) => {
        this.setState({answ: answ,});
    }

    render() {
        const { opened } = this.state;
        const body = this.renderBody();
        const { bodyText, headerText, footerText } = this.props;

        return (
            <div className="docked-widget">
                <Transition in={opened} timeout={10} onExited={this.handleWidgetExit}>
                    {status => (
                        <div className={`widget widget-${status}`}>
                            <div className="widget-header">
                                <div className="widget-header-title">
                                    {headerText}
                                </div>

                            </div>
                            <div className="widget-body">
                                {bodyText}
                                <Table
                                    name={this.state.msg}
                                    changeMsg = {this.message}
                                />
                            </div>
                            <div className="widget-footer">
                                {footerText}
                                <Form
                                    changeMsg = {this.message}
                                />
                                <button
                                    type="button"
                                    className="widget-footer-shift"
                                    onClick={this.handleToggleOpen}
                                    onKeyPress={this.handleToggleOpen}
                                >
                                    X
                                </button>
                            </div>
                        </div>
                    )}
                </Transition>
                {body}
            </div>
        );
    }
}

function answer(props, answ) {
    props.changeMsg("");
}

function Table (props) {

    if (props.name !== "")
        return <table width="100%" id="answer">
            <tr>
                <td colspan="2" onClick={() => answer(props, "yes")}>Да!</td>
                <td colspan="2">Да!</td>
            </tr>
            <tr>
                <td>Да!</td>
                <td>Да!</td>
                <td>Да!</td>
                <td>Да!</td>
            </tr>
            <tr>
                <td colspan="3">Да, везите скорей!</td>
                <td>Да!</td>
            </tr>
        </table>;
    return <div></div>;
}

Widget.propTypes = {
    headerText: PropTypes.string,
    bodyText: PropTypes.string,
    footerText: PropTypes.string,
};

Widget.defaultProps = {
    headerText: 'Бот',
    bodyText: "Я онлайн",
    footerText: 'Footer',
};

export default Widget;
