import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { Transition } from 'react-transition-group';
import './widget.scss';
import ReactDOM from "react-dom";
import Form from "./Form";

class Widget extends Component {
    state = {
        opened: false,
        showDock: true,
        msg: "rrrrrrr",
    }

    handleToggleOpen = () => {
        this.setState((prev) => {
            let { showDock } = prev;
            if (!prev.opened) {
                showDock = false;
            }
            return {
                showDock,
                opened: !prev.opened,
            };
        });
    }

    handleWidgetExit = () => {
        this.setState({
            showDock: true,
        });
    }

    renderBody = () => {
        const { showDock } = this.state;
        const { send } = this.state;

        if (!showDock && send) return (
            <div>hello yopt</div>
        );

        if (!showDock) return '';

        return (
            <button
                type="button"
                className="dock"
                onClick={this.handleToggleOpen}
                onKeyPress={this.handleToggleOpen}
            >
                ^ OPEN ^
            </button>
        );
    }

    message(msg) {
        this.setState({msg});
    }

    render() {
        const { opened } = this.state;
        const body = this.renderBody();
        const { bodyText, headerText, footerText } = this.props;

        return (
            <div className="docked-widget">
                <Transition in={opened} timeout={10} onExited={this.handleWidgetExit}>
                    {status => (
                        <div className={`widget widget-${status}`}>
                            <div className="widget-header">
                                <div className="widget-header-title">
                                    {headerText}
                                </div>

                            </div>
                            <div className="widget-body">
                                {bodyText}
                                <Form
                                    message = {this.msg}
                                    changeMsg = {this.message}
                                />
                            </div>
                            <div className="widget-footer">
                                {footerText}
                                <button
                                    type="button"
                                    className="widget-footer-shift"
                                    onClick={this.handleToggleOpen}
                                    onKeyPress={this.handleToggleOpen}
                                >
                                    X
                                </button>
                            </div>
                        </div>
                    )}
                </Transition>
                {body}
            </div>
        );
    }
}

Widget.propTypes = {
    headerText: PropTypes.string,
    bodyText: PropTypes.string,
    footerText: PropTypes.string,
};

Widget.defaultProps = {
    headerText: 'Бот',
    bodyText: 'Привет,я онлайн',
    footerText: 'Footer',
};

export default Widget;
