import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { Transition } from 'react-transition-group';
import './widget.scss';
import Form from "./Form";
import Table from "./Table";
import Header from "./Header";

class Widget extends Component {
    constructor(props) {
        super(props);
        this.append = this.append.bind(this);
    }

    append(value) {
        const {text} = this.state
        this.setState({
            text: text.concat(value),
        });

    }
    state = {
        opened: false,
        showDock: true,
        msg: "",
        answ: "",
        text: [],
        header: "",
        botMsg: "",
    }

    handleToggleOpen = () => {
        this.setState((prev) => {
            let { showDock } = prev;
            if (!prev.opened) {
                showDock = false;
            }
            return {
                showDock,
                opened: !prev.opened,
            };
        });
    }

    handleWidgetExit = () => {
        this.setState({
            showDock: true,
        });
    }

    renderBody = () => {
        const { showDock } = this.state;

        if (!showDock) return '';

        return (
            <div
                className="widget-footer-msg"
                onClick={this.handleToggleOpen}
                onKeyPress={this.handleToggleOpen}
            />
        );
    }

    message = (msg) => {
        this.setState({msg: msg, header: "-dialog"});
        if (msg !== "")
            this.setState({botMsg: msg,});
    }

    answer = (answ) => {
        this.setState({answ: answ,});
    }

    render() {
        const { opened } = this.state;
        const body = this.renderBody();
        const { bodyText, headerText, footerText } = this.props;

        return (
            <div className="docked-widget">
                <Transition in={opened} timeout={10} onExited={this.handleWidgetExit}>
                    {status => (
                        <div className={`widget widget-${status}`}>
                            <Header name = {this.state.header}/>
                            <div className="widget-body">
                                <Table
                                    name={this.state.botMsg}
                                    changeMsg = {this.message}
                                    append = {this.append}
                                />
                                <div>
                                    {this.state.text.map(x => (<div>{x}</div>))}
                                </div>
                            </div>
                            <div className="widget-footer">
                                <Form
                                    changeMsg = {this.message}
                                />
                                <div className="widget-footer-slash"></div>
                                <div className="widget-footer-slash2"></div>
                                <div
                                    className="widget-footer-shift"
                                    onClick={this.handleToggleOpen}
                                    onKeyPress={this.handleToggleOpen}
                                />
                            </div>
                        </div>
                    )}
                </Transition>
                {body}
            </div>
        );
    }
}

Widget.propTypes = {
    headerText: PropTypes.string,
    bodyText: PropTypes.string,
    footerText: PropTypes.string,
};

Widget.defaultProps = {
    headerText: "widget-header",
    bodyText: "Я онлайн",
    footerText: 'Footer',
};

export default Widget;
